con:config.script:
def count = Integer.parseInt(context.getProperty("task-assignement-max-loop"))
context.setProperty("task-assignement-max-loop", String.valueOf(--count))

if (count >= 0){
 testRunner.gotoStep(testRunner.testCase.getTestStepIndexByName('Wait task assignement'));
} else {
 testRunner.fail("Too many iterations to wait task assignement");
}
