con:config.script:
def count = Integer.parseInt(context.getProperty("task-assignement-max-loop"))
context.setProperty("task-assignement-max-loop", String.valueOf(--count))

if (count >= 0){
 testRunner.gotoStep(testRunner.testCase.getTestStepIndexByName('Wait task assignement'));
} else {
 testRunner.fail("Too many iterations to wait task assignement");
}

con:configuration.scriptText:
//Define Groovy Utils and holder for validating the XML reponse content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

//Define the NameSpace
holder.namespaces["soap"] = "http://schemas.xmlsoap.org/soap/envelope/"
holder.namespaces["ns"] = "http://petals.ow2.org/components/flowable/generic/1.0"

def count = Integer.parseInt(holder.getNodeValue("count(//soap:Envelope/soap:Body/ns:getTasksResponse/ns:tasks/ns:task)"))
if (count > 0) {
   assert count == 1
   def expectedProcInstId = context.getProperty("vacationRequestId")
   def processInstanceId = holder.getNodeValue("//soap:Envelope/soap:Body/ns:getTasksResponse/ns:tasks/ns:task/@process-instance-identifier")

   assert expectedProcInstId != null
   assert processInstanceId != null
   assert expectedProcInstId == processInstanceId
}

